// src/components/SeatSelection.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './SeatSelection.css';

const SeatSelection = () => {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const navigate = useNavigate();

  const toggleSeatSelection = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seatNumber)) {
        // If seat is already selected, remove it
        return prevSelectedSeats.filter((seat) => seat !== seatNumber);
      } else {
        // If seat is not selected, add it
        return [...prevSelectedSeats, seatNumber];
      }
    });
  };

  // Dummy seats data
  const seats = Array.from({ length: 40 }, (_, i) => i + 1);

  const selectedSeatsText = selectedSeats.length === 0 ? 'Selected Seat' : `Selected Seats (${selectedSeats.length})`;

  const confirmButtonText = selectedSeats.length === 0 ? 'Proceed Payment' : `Proceed payment for ${selectedSeats.length} ${selectedSeats.length > 1 ? 'seats' : 'seat'}`;

  return (
    <div className="seat-selection">
      <h1>Select Seats </h1>
      <div className="seats">
        {seats.map((seat) => (
          <button
            key={seat}
            className={`seat ${selectedSeats.includes(seat) ? 'selected' : ''}`}
            onClick={() => toggleSeatSelection(seat)}
          >
            {seat}
          </button>
        ))}
      </div>
      {selectedSeats.length > 0 && (
        <div>
          <p>{selectedSeatsText}: {selectedSeats.join(', ')}</p>
        </div>
      )}
      <button
        className="confirm-button"
        onClick={() => navigate('/payment', { state: { selectedSeats } })}
        disabled={selectedSeats.length === 0}
      >
        {confirmButtonText}
      </button>
    </div>
  );
};

export default SeatSelection;
