import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './SeatSelection.css'; // Import the CSS file

const SeatSelection = () => {
  const { busId } = useParams();
  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeatSelection = seatNumber => {
    setSelectedSeats(prevSelectedSeats => {
      if (prevSelectedSeats.includes(seatNumber)) {
        // If seat is already selected, remove it
        return prevSelectedSeats.filter(seat => seat !== seatNumber);
      } else {
        // If seat is not selected, add it
        return [...prevSelectedSeats, seatNumber];
      }
    });
  };

  // Function to determine the seat position (left or right) based on the seat number
  const getSeatPosition = seatNumber => {
    return seatNumber % 2 === 0 ? 'right' : 'left';
  };

  // Function to determine the seat row (1, 2, 3, ...) based on the seat number
  const getSeatRow = seatNumber => {
    return Math.ceil(seatNumber / 2);
  };

  // Dummy seats data (40 seats in total)
  const seats = Array.from({ length: 40 }, (_, i) => i + 1);

  return (
    <div className="seat-selection">
      <h1>Select Seats for Bus {busId}</h1>
      <div className="seats">
        {seats.map(seat => (
          <button
            key={seat}
            className={`seat ${selectedSeats.includes(seat) ? 'selected' : ''}`}
            onClick={() => toggleSeatSelection(seat)}
            style={{
              gridRow: `${getSeatRow(seat)} / span 1`, // Set the row span to 1
              gridColumn: getSeatPosition(seat) // Set the column position (left or right)
            }}
          >
            {seat}
          </button>
        ))}
      </div>
      <Link to="/confirmation">
        <button className="confirm-button">Confirm Booking</button>
      </Link>
    </div>
  );
};

export default SeatSelection;
