// src/components/BookingConfirmation.js
import React, { useEffect, useContext } from 'react';
import './BookingConfirmation.css';
import { UserContext } from '../UserContext';
import { sendEmail } from '../utils/sendEmail';

const BookingConfirmation = () => {
  const { userEmail } = useContext(UserContext);

  useEffect(() => {
    if (userEmail) {
      sendEmail(userEmail, 'Your seat information here'); // Update with actual seat info
    }
  }, [userEmail]);

  return (
    <div className="booking-confirmation">
      <div className="message-box">
        <h1>Yay!</h1>
        <p>Your Seats are booked</p>
        <p>Enjoy your upcoming journey!</p>
        <p>The ticket has been sent to your email: {userEmail}</p>
      </div>
      <video className="background-video" autoPlay loop muted>
        <source src="/path-to-your-video.mp4" type="video/mp4" />
      </video>
    </div>
  );
};

export default BookingConfirmation;
