// src/components/SeatSelection.js

import React, { useState } from 'react';
import './SeatSelection.css';

const SeatSelection = ({ onSelect }) => {
  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeatSelection = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) =>
      prevSelectedSeats.includes(seatNumber)
        ? prevSelectedSeats.filter((seat) => seat !== seatNumber)
        : [...prevSelectedSeats, seatNumber]
    );
  };

  const handleSeatClick = (seatNumber) => {
    toggleSeatSelection(seatNumber);
    onSelect(selectedSeats);
  };

  return (
    <div className="bus">
      <div className="bus-row">
        <div className="left-side">
          {Array.from({ length: 20 }, (_, i) => (
            <div
              key={i + 1}
              className={`seat ${selectedSeats.includes(i + 1) ? 'selected' : ''}`}
              onClick={() => handleSeatClick(i + 1)}
            >
              {i + 1}
            </div>
          ))}
        </div>
        <div className="right-side">
          {Array.from({ length: 20 }, (_, i) => (
            <div
              key={i + 21}
              className={`seat ${selectedSeats.includes(i + 21) ? 'selected' : ''}`}
              onClick={() => handleSeatClick(i + 21)}
            >
              {i + 21}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SeatSelection;
