import './SeatSelection.css';
import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './SeatSelection.css'; // Import the CSS file

const SeatSelection = () => {
  const { busId } = useParams();
  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeatSelection = (seatNumber) => {
    setSelectedSeats((prev) =>
      prev.includes(seatNumber)
        ? prev.filter((seat) => seat !== seatNumber)
        : [...prev, seatNumber]
    );
  };

  // Generate seat numbers with alternating left and right
  const seats = [];
  for (let i = 1; i <= 20; i += 2) {
    seats.push([i, i + 1]);
  }

  return (
    <div className="seat-selection">
      <h1>Select Seats for Bus {busId}</h1>
      <div className="seats">
        {seats.map((row, rowIndex) => (
          <div key={rowIndex} className="seat-row">
            <div className="left-seats">
              <h2>Left Seats</h2>
              {row.map((seat) => (
                <button
                  key={seat}
                  className={selectedSeats.includes(seat) ? 'selected' : ''}
                  onClick={() => toggleSeatSelection(seat)}
                >
                  {seat}
                </button>
              ))}
            </div>
            <div className="right-seats">
              <h2>Right Seats</h2>
              {row.map((seat) => (
                <button
                  key={seat + 1}
                  className={selectedSeats.includes(seat + 1) ? 'selected' : ''}
                  onClick={() => toggleSeatSelection(seat + 1)}
                >
                  {seat + 1}
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
      <Link to="/confirmation">
        <button>Confirm Booking</button>
      </Link>
    </div>
  );
};

export default SeatSelection;
