// src/components/BookingConfirmation.js
import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import sendEmail from '../utils/sendEmail';
import './BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();

  useEffect(() => {
    const selectedSeats = location.state?.selectedSeats || [];
    const totalAmount = location.state?.totalAmount || 0;
    const userEmail = location.state?.userEmail || '';

    if (userEmail) {
      sendEmail(
        userEmail,
        'Booking Confirmation',
        `Your seats ${selectedSeats.join(', ')} have been booked. Total Amount: Rs.${totalAmount}`
      )
        .then((response) => {
          console.log('Email sent successfully', response.status, response.text);
        })
        .catch((error) => {
          console.error('Failed to send email', error);
        });
    }
  }, [location.state]);

  return (
    <div className="booking-confirmation">
      <div className="message-box">
        <h1>Yay!</h1>
        <p>Your Seats are booked</p>
        <p>Enjoy your upcoming journey!</p>
        <p>The ticket has been sent to your mail</p>
      </div>
    </div>
  );
};

export default BookingConfirmation;
