// export default SeatSelection;import React, { useState } from 'react';import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './SeatSelection.css';
import React, { useState } from 'react';

const SeatSelection = () => {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const navigate = useNavigate();
  const { busId } = useParams();

  const seatPrices = {
    1: 550, 4: 550, 5: 550, 8: 550, 9: 550, 12: 550, 13: 550, 16: 550,
    17: 550, 20: 550, 21: 550, 24: 550, 25: 550, 28: 550, 29: 550, 32: 550,
    33: 550, 36: 550, 37: 550, 40: 550,
    2: 500, 3: 500, 6: 500, 7: 500, 10: 500, 11: 500, 14: 500, 15: 500,
    18: 500, 19: 500, 22: 500, 23: 500, 26: 500, 27: 500, 30: 500, 31: 500,
    34: 500, 35: 500, 38: 500, 39: 500
  };

  const toggleSeatSelection = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seatNumber)) {
        setTotalAmount((prevAmount) => prevAmount - seatPrices[seatNumber]);
        return prevSelectedSeats.filter((seat) => seat !== seatNumber);
      } else {
        setTotalAmount((prevAmount) => prevAmount + seatPrices[seatNumber]);
        return [...prevSelectedSeats, seatNumber];
      }
    });
  };

  const seats = Array.from({ length: 40 }, (_, i) => i + 1);

  const confirmButtonText = selectedSeats.length === 0
    ? 'Proceed Payment'
    : `Proceed payment for ${selectedSeats.length} ${selectedSeats.length > 1 ? 'seats' : 'seat'} (Total: Rs.${totalAmount})`;

  const handleProceed = () => {
    if (selectedSeats.length > 0) {
      navigate('/payment', { state: { selectedSeats, totalAmount } });
    }
  };

  return (
    <div className="seat-selection">
      <h1>Select Seats for Bus {busId}</h1>
      <div className="seats">
        {seats.map((seat) => (
          <button
            key={seat}
            className={`seat ${selectedSeats.includes(seat) ? 'selected' : ''}`}
            onClick={() => toggleSeatSelection(seat)}
          >
            {seat}
          </button>
        ))}
      </div>
      {selectedSeats.length > 0 && (
        <div>
          <p>Selected Seats: {selectedSeats.join(', ')}</p>
        </div>
      )}
      <button
        className="confirm-button"
        onClick={handleProceed}
        disabled={selectedSeats.length === 0}
      >
        {confirmButtonText}
      </button>
    </div>
  );
};

export default SeatSelection;
