import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import qrImage from '../assets/paytmsiva.jpeg';
import './Payment.css';

const Payment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const selectedSeats = location.state?.selectedSeats || [];
  const totalAmount = location.state?.totalAmount || 0;

  const handlePaymentConfirmation = () => {
    alert('Payment confirmed!');
    navigate('/confirmation');
  };

  return (
    <div className="payment-container">
      <h1>Payment Page(ont refresh the page)</h1>
      <p>Selected Seats: {selectedSeats.join(', ')}</p>
      <p>Total Amount: Rs.{totalAmount}</p>
      <div className="qr-code-container">
        <p>Please scan the QR code below to make the payment:</p>
        <img src={qrImage} alt="QR Code" className="qr-code" />
      </div>
      <button onClick={handlePaymentConfirmation}>I Have Paid</button>
    </div>
  );
};

export default Payment;
