// src/components/BookingConfirmation.js
import React, { useEffect, useContext, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { UserContext } from '../contexts/UserContext';
import sendEmail from '../utils/sendEmail';
import './BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();
  const { userEmail } = useContext(UserContext);
  const selectedSeats = location.state?.selectedSeats || [];
  const totalAmount = location.state?.totalAmount || 0;
  const [emailStatus, setEmailStatus] = useState(null);

  useEffect(() => {
    if (userEmail) {
      const subject = 'Your Booking Confirmation';
      const message = `Your booking is confirmed for seats: ${selectedSeats.join(', ')}. Total Amount: Rs.${totalAmount}`;

      sendEmail(userEmail, subject, message)
        .then(() => {
          setEmailStatus('Email sent successfully!');
        })
        .catch((error) => {
          setEmailStatus(`Failed to send email: ${error.text}`);
        });
    }
  }, [userEmail, selectedSeats, totalAmount]);

  return (
    <div className="booking-confirmation">
      <div className="message-box">
        <h1>Yay!</h1>
        <p>Your Seats are booked</p>
        <p>Enjoy your upcoming journey!</p>
        {emailStatus && <p>{emailStatus}</p>}
      </div>
    </div>
  );
};

export default BookingConfirmation;
