// src/components/Home.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

import './Home.css'; // Import the CSS file for background image

const Home = () => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');

  const areas = ['Chennai', 'Salem', 'Trichy', 'Coimbatore', 'Erode'];

  return (
    <div className="home">
      <h1>Welcome to Bus Ticket Booking</h1>
      <div>
        <label>
          From:
          <select value={from} onChange={(e) => setFrom(e.target.value)}>
            <option value="" disabled>Select Area</option>
            {areas.map((area) => (
              <option key={area} value={area}>
                {area}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          To:
          <select value={to} onChange={(e) => setTo(e.target.value)}>
            <option value="" disabled>Select Area</option>
            {areas.map((area) => (
              <option key={area} value={area}>
                {area}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
      </div>
      <Link to={`/buses?from=${from}&to=${to}&date=${date}`}>
        <button disabled={!from || !to || !date}>View Available Buses</button>
      </Link>
    </div>
  );
};

export default Home;
