import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import sendEmail from '../utils/sendEmail'; // Correct import path
import './BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();

  useEffect(() => {
    const email = location.state?.email || '';
    const selectedSeats = location.state?.selectedSeats || [];
    
    if (email && selectedSeats.length > 0) {
      sendEmail(email, selectedSeats); // Send the email
    }
  }, [location.state]);

  return (
    <div className="booking-confirmation">
      <video autoPlay loop muted className="background-video">
        <source src="/path-to-your-video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="message-box">
        <h1>Yay!</h1>
        <p>Your Seats are booked</p>
        <p>Enjoy your upcoming journey!</p>
        <p>The ticket has been sent to your email</p>
      </div>
    </div>
  );
};

export default BookingConfirmation;
