// src/components/BusList.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const BusList = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const from = params.get('from');
  const to = params.get('to');

  // Dummy data for buses
  const buses = [
    { id: 1, name: 'Tranzking Travels', seats: 40, from: 'Chennai', to: 'Salem' },
    { id: 2, name: 'Finch Travels', seats: 40, from: 'Chennai', to: 'Trichy' },    
    { id: 3, name: 'Vetri Travels', seats: 40, from: 'Chennai', to: 'Erode' },
    { id: 4, name: 'Parveen Travels', seats: 40, from: 'Chennai', to: 'Coimbatore' },

    { id: 5, name: 'SBLT Travels', seats: 50, from: 'Trichy', to: 'Coimbatore' },
    { id: 6, name: 'SRMS Travels', seats: 50, from: 'Trichy', to: 'Chennai' },
    { id: 7, name: 'Rathimeena Travels', seats: 50, from: 'Trichy', to: 'Erode' },
    { id: 8, name: 'RKT Travels', seats: 50, from: 'Trichy', to: 'Salem' },

    { id: 9, name: 'KMS Travels', seats: 40, from: 'Erode', to: 'Salem' },
    { id: 10, name: 'Vetri Travels', seats: 40, from: 'Erode', to: 'Trichy' },    
    { id: 11, name: 'RKT Travels', seats: 40, from: 'Erode', to: 'Chennai' },
    { id: 12, name: 'SBLT Travels', seats: 40, from: 'Erode', to: 'Coimbatore' },

    { id: 13, name: 'Swamy Ayyappa Travels', seats: 50, from: 'Salem', to: 'Coimbatore' },
    { id: 14, name: 'TAT Travels', seats: 50, from: 'Salem', to: 'Chennai' },
    { id: 15, name: 'SPS Travels', seats: 50, from: 'Salem', to: 'Erode' },
    { id: 16, name: 'Krishna Travels', seats: 50, from: 'Salem', to: 'Trichy' },

    { id: 17, name: 'Swamy Ayyappa Travels', seats: 50, from: 'Coimbatore', to: 'Coimbatore' },
    { id: 18, name: 'TAT Travels', seats: 50, from: 'Coimbatore', to: 'Chennai' },
    { id: 19, name: 'SPS Travels', seats: 50, from: 'Salem', to: 'Erode' },
    { id: 20, name: 'Krishna Travels', seats: 50, from: 'Salem', to: 'Trichy' },
  ];

  const availableBuses = buses.filter(
    (bus) => bus.from === from && bus.to === to
  );

  return (
    <div className="bus-list">
      <h1>Available Buses from {from} to {to}</h1>
      <ul>
        {availableBuses.length > 0 ? (
          availableBuses.map(bus => (
            <li key={bus.id}>
              {bus.name} - {bus.seats} seats
              <Link to={`/seats/${bus.id}`}>
                <button>Select Seats</button>
              </Link>
            </li>
          ))
        ) : (
          <li>No buses available for the selected route.</li>
        )}
      </ul>
    </div>
  );
};

export default BusList;
