// src/components/Home.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import './return-content.css';
import SeatSelection from './SeatSelection';

const Home = () => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const areas = ['Chennai', 'Salem', 'Trichy', 'Coimbatore', 'Erode'];

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    const today = new Date().toISOString().split('T')[0];
    if (selectedDate < today) {
      setErrorMessage('Select a date from today onwards');
    } else {
      setErrorMessage('');
      setDate(selectedDate);
    }
  };

  const handleAreaChange = (setter) => (e) => {
    setter(e.target.value);
    if (from === to) {
      setErrorMessage('Pickup and drop-off locations cannot be the same');
    } else {
      setErrorMessage('');
    }
  };

  const handleButtonClick = (e) => {
    if (from === to) {
      e.preventDefault();
      setErrorMessage('Pickup and drop-off locations cannot be the same');
    } else {
      setErrorMessage('');
      // Redirect or perform other action here
    }
  };

  const handleSeatSelection = (selectedSeats) => {
    console.log(selectedSeats); // Handle selected seats here
  };

  return (
    <div className="home">
      <div className="carousel">
        {/* Carousel content */}
      </div>
      <div className="highlight-box">
        <h1>Welcome to Bus Ticket Booking</h1>
        <div>
          <label>
            From:
            <select value={from} onChange={handleAreaChange(setFrom)}>
              <option value="" disabled>Select Area</option>
              {areas.map((area) => (
                <option key={area} value={area}>
                  {area}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>
            To:
            <select value={to} onChange={handleAreaChange(setTo)}>
              <option value="" disabled>Select Area</option>
              {areas.map((area) => (
                <option key={area} value={area}>
                  {area}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>
            Date:
            <input
              type="date"
              value={date}
              onChange={handleDateChange}
              min={new Date().toISOString().split('T')[0]} // Set the minimum date to today
            />
          </label>
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <Link to={`/buses?from=${from}&to=${to}&date=${date}`}>
          <button onClick={handleButtonClick} disabled={!from || !to || !date}>View Available Buses</button>
        </Link>
      </div>
      <SeatSelection onSelect={handleSeatSelection} />
    </div>
  );
};

export default Home;
